mapper.insert:
CREATE TABLE IF NOT EXISTS api_key (
  primary_principal VARCHAR(200)   NOT NULL,
  domain            VARCHAR(200)   NOT NULL,
  token             VARCHAR(200)   NOT NULL,
  principals        ${BINARY_TYPE} NOT NULL,
  created           TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,


  CONSTRAINT pk_api_key_primaryprincipal_domain_principals PRIMARY KEY (primary_principal, domain, principals),
  CONSTRAINT uk_api_key_domain_token UNIQUE (domain, token)
);

ALTER TABLE api_key ADD COLUMN IF NOT EXISTS created TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP;

mapper.select:
SELECT * FROM api_key
 WHERE primary_principal = #{primaryPrincipal, typeHandler=EncryptedStringTypeHandler}
   AND domain = #{domain};

mapper.insert:
MERGE INTO api_key (primary_principal, domain, token, principals , created)
    VALUES (#{primaryPrincipal, typeHandler=EncryptedStringTypeHandler},
            #{domain}, #{token}, #{principals} , #{created});

mapper.delete:
DELETE FROM api_key
    WHERE primary_principal = #{primaryPrincipal, typeHandler=EncryptedStringTypeHandler}
    AND domain = #{domain};

DELETE FROM api_key
    WHERE domain = #{domain}
      AND token = #{token};

mapper.select:
SELECT *
  FROM api_key
WHERE domain = #{domain}
ORDER BY created;

SELECT *
  FROM api_key
WHERE domain = #{domain}
  AND created > #{created}
ORDER BY created;

SELECT count(*)
  FROM api_key
 WHERE domain = #{domain};

mapper.update:
INSERT INTO api_key (primary_principal
                    , domain
                    , token
                    , principals
                    , created)
VALUES (#{primaryPrincipal, typeHandler=EncryptedStringTypeHandler}
        , #{domain}
        , #{token}
        , #{principals}
        , #{created})
ON CONFLICT (domain, token)
DO UPDATE SET
  token = #{token}
  , principals = #{principals}
  , created = #{created};

MERGE INTO api_key KEY (primary_principal, domain, token)
VALUES (#{primaryPrincipal, typeHandler=EncryptedStringTypeHandler}
        , #{domain}
        , #{token}
        , #{principals}
        , #{created});

mapper.select:
SELECT *
FROM api_key
WHERE domain = #{domain}
ORDER BY created
LIMIT #{limit}
OFFSET #{skip};

  SELECT *
    FROM api_key
   WHERE created > #{created}
ORDER BY created
   LIMIT #{limit};
