mapper.insert:
CREATE TABLE IF NOT EXISTS api_key_v2 (
  username          VARCHAR(200)   NOT NULL,
  principals        ${BINARY_TYPE} NOT NULL,

  domain            VARCHAR(200)   NOT NULL,
  access_key        VARCHAR(200)   NOT NULL,
  secret            VARCHAR(200)   NOT NULL,

  created           TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,


  CONSTRAINT pk_api_key_v2_username_domain_principals PRIMARY KEY (username, domain, principals),
  CONSTRAINT uk_api_key_v2_domain_access_key UNIQUE (domain, access_key)
);

CREATE INDEX IF NOT EXISTS idx_api_key_v2_domain_created ON api_key_v2 (domain, created);
CREATE INDEX IF NOT EXISTS idx_api_key_v2_created ON api_key_v2 (created);
CREATE INDEX IF NOT EXISTS idx_api_key_v2_principals ON api_key_v2 (principals);

mapper.select:
SELECT *
  FROM api_key_v2
 WHERE username   = #{username}
   AND domain = #{domain};

SELECT *
  FROM api_key_v2
 WHERE domain = #{domain}
   AND access_key = #{accessKey};

mapper.insert:
INSERT INTO api_key_v2
       (username, principals, domain, access_key, secret , created)
VALUES (#{username}, #{principals}, #{domain}, #{accessKey}, #{secret} , #{created});

mapper.delete:
DELETE
  FROM api_key_v2
 WHERE domain = #{domain}
   AND username = #{username}
   AND access_key = #{accessKey};

mapper.select:
SELECT *
  FROM api_key_v2
WHERE domain = #{domain}
ORDER BY created;

SELECT *
  FROM api_key_v2
WHERE created < #{created}
ORDER BY created;

SELECT count(*)
  FROM api_key_v2
 WHERE domain = #{domain};

mapper.update:
UPDATE api_key_v2
   SET principals = #{principals}
 WHERE domain = #{domain}
   AND username = #{username}
   AND access_key = #{accessKey};

mapper.select:
  SELECT *
    FROM api_key_v2
   WHERE domain = #{domain}
ORDER BY created
   LIMIT #{limit}
  OFFSET #{skip};
