mapper.insert:
-- -- each format has an asset blob table which they can extend -- CREATE TABLE IF NOT EXISTS ${format}_asset_blob ( asset_blob_id INT GENERATED BY DEFAULT AS IDENTITY, blob_ref VARCHAR NOT NULL, blob_size BIGINT NOT NULL, content_type VARCHAR NOT NULL, checksums ${JSON_TYPE} NOT NULL, blob_created TIMESTAMP WITH TIME ZONE NOT NULL, created_by VARCHAR, created_by_ip VARCHAR,
CONSTRAINT pk_${format}_asset_blob_id PRIMARY KEY (asset_blob_id), CONSTRAINT uk_${format}_asset_blob_ref UNIQUE (blob_ref) );
ALTER TABLE ${format}_asset_blob ADD COLUMN IF NOT EXISTS added_to_repository TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP; CREATE INDEX IF NOT EXISTS idx_${format}_asset_blob_added_to_repository ON ${format}_asset_blob (added_to_repository);
CREATE INDEX IF NOT EXISTS idx_${format}_asset_blob_created ON ${format}_asset_blob (blob_created); --

mapper.select:
SELECT * FROM ${format}_asset_blob

  WHERE asset_blob_id > #{continuationToken}

ORDER BY asset_blob_id LIMIT #{limit};

SELECT B.* FROM ${format}_asset_blob B LEFT JOIN ${format}_asset A USING (asset_blob_id) WHERE A.asset_blob_id IS NULL
AND B.asset_blob_id > #{continuationToken}
AND B.blob_created <= NOW() - #{blobCreatedDelayMinute} * INTERVAL '1' MINUTE
AND B.blob_created <= DATEADD(MINUTE, 0-#{blobCreatedDelayMinute}, NOW())
LIMIT #{limit};

mapper.insert:
INSERT INTO ${format}_asset_blob (blob_ref, blob_size, content_type, checksums,
                                  blob_created, created_by, created_by_ip
    , added_to_repository)
    VALUES (#{blobRef}, #{blobSize}, #{contentType}, #{checksums},
      #{blobCreated}, #{createdBy}, #{createdByIp}
    , #{addedToRepository});

mapper.select:
SELECT * FROM ${format}_asset_blob
WHERE blob_ref LIKE '%:%'

  AND asset_blob_id > #{continuationToken}

ORDER BY asset_blob_id LIMIT #{limit};
