mapper.insert:
-- -- each format has an asset table which they can extend -- CREATE TABLE IF NOT EXISTS ${format}_asset ( asset_id INT GENERATED BY DEFAULT AS IDENTITY, repository_id INT NOT NULL, path VARCHAR NOT NULL, kind VARCHAR NOT NULL, component_id INT, asset_blob_id INT, last_downloaded TIMESTAMP WITH TIME ZONE, attributes ${JSON_TYPE} NOT NULL, created TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP, last_updated TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP, asset_blob_size BIGINT,
CONSTRAINT pk_${format}_asset_id PRIMARY KEY (asset_id), CONSTRAINT uk_${format}_asset_repository_path UNIQUE (repository_id, path),
CONSTRAINT fk_${format}_asset_repository FOREIGN KEY (repository_id) REFERENCES ${format}_content_repository (repository_id), CONSTRAINT fk_${format}_asset_component FOREIGN KEY (component_id) REFERENCES ${format}_component (component_id), CONSTRAINT fk_${format}_asset_blob FOREIGN KEY (asset_blob_id) REFERENCES ${format}_asset_blob (asset_blob_id) );
CREATE INDEX IF NOT EXISTS idx_${format}_asset_kind ON ${format}_asset (kind); CREATE INDEX IF NOT EXISTS idx_${format}_asset_component ON ${format}_asset (component_id); CREATE INDEX IF NOT EXISTS idx_${format}_asset_blob ON ${format}_asset (asset_blob_id);
CREATE INDEX IF NOT EXISTS idx_${format}_asset_last_downloaded ON ${format}_asset (last_downloaded); CREATE INDEX IF NOT EXISTS idx_${format}_asset_path ON ${format}_asset (path);
ALTER TABLE ${format}_asset ADD COLUMN IF NOT EXISTS asset_blob_size BIGINT;
--

mapper.select:
SELECT COUNT(*) FROM ${format}_asset WHERE repository_id = #{repositoryId}
     AND kind = #{kind}
     AND (${filter});

SELECT * FROM ${format}_asset as asset WHERE repository_id = #{repositoryId}
     AND asset_id > #{continuationToken}
     AND kind = #{kind}
     AND (${filter})
ORDER BY asset_id LIMIT #{limit};

SELECT * FROM ${format}_asset
WHERE repository_id IN

    #{repositoryId}

   AND asset_id > #{continuationToken}
   AND kind = #{kind}
   AND (${filter})
ORDER BY asset_id LIMIT #{limit};

SELECT * FROM ${format}_asset a INNER JOIN ${format}_asset_blob ab USING (asset_blob_id)
WHERE a.repository_id = #{repositoryId}
 AND ab.added_to_repository >= #{addedToRepository}

 AND (${filter})
ORDER BY ab.added_to_repository LIMIT #{limit}

SELECT * FROM ${format}_asset a INNER JOIN ${format}_asset_blob ab USING (asset_blob_id)
WHERE a.repository_id = #{repositoryId}
AND ab.added_to_repository >= #{startAddedToRepository} AND ab.added_to_repository < #{endAddedToRepository}

 AND (${filter})
ORDER BY ab.added_to_repository LIMIT #{limit}

