mapper.select:
SELECT * FROM ${format}_asset WHERE repository_id = #{repositoryId} AND path IN

  #{path}
;

SELECT * FROM ${format}_asset A INNER JOIN ${format}_asset_blob USING (asset_blob_id)
INNER JOIN ( SELECT ap.format, ap.repository_id, ap.component_id, ap.asset_id FROM search_assets ap
(${assetFilter})
AND
#{componentId}
) content ON '${format}' = content.format AND A.repository_id = content.repository_id AND A.component_id = content.component_id AND A.asset_id = content.asset_id
#{componentId}
ORDER BY A.asset_id

