mapper.insert:
-- -- each format has a component table which they can extend -- CREATE TABLE IF NOT EXISTS ${format}_component ( component_id INT GENERATED BY DEFAULT AS IDENTITY, repository_id INT NOT NULL, namespace VARCHAR NOT NULL, name VARCHAR NOT NULL, kind VARCHAR NOT NULL, version VARCHAR NOT NULL, attributes ${JSON_TYPE} NOT NULL, created TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP, last_updated TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
CONSTRAINT pk_${format}_component_id PRIMARY KEY (component_id), CONSTRAINT uk_${format}_component_coordinate UNIQUE (repository_id, namespace, name, version),
CONSTRAINT fk_${format}_component_repository FOREIGN KEY (repository_id) REFERENCES ${format}_content_repository (repository_id) );
CREATE INDEX IF NOT EXISTS idx_${format}_component_kind ON ${format}_component (kind);
--Note: index intentionally not added because this column is intended to be used -- with the component_id which is indexed ALTER TABLE ${format}_component ADD COLUMN IF NOT EXISTS entity_version INT;

mapper.select:
SELECT COUNT(*) FROM ${format}_component WHERE repository_id = #{repositoryId}
     AND kind = #{kind}
     AND (${filter});

SELECT * FROM ${format}_component WHERE repository_id = #{repositoryId}
     AND component_id > #{continuationToken}
     AND kind = #{kind}
     AND (${filter})
ORDER BY component_id LIMIT #{limit};

SELECT * FROM ${format}_component
WHERE repository_id IN

  #{repositoryId}

 AND component_id > #{continuationToken}
ORDER BY component_id LIMIT #{limit};

mapper.insert:
INSERT INTO ${format}_component (repository_id, namespace, name, kind, version, attributes
    , entity_version
) VALUES (#{component.repositoryId}, #{component.namespace}, #{component.name},
    #{component.kind}, #{component.version}, #{component.attributes} , 1
);

mapper.delete:
DELETE FROM ${format}_component WHERE
     component_id IN (SELECT component_id FROM ${format}_component WHERE 
    repository_id = #{repositoryId}
     LIMIT #{limit});
